import styled from 'styled-components';

export const LanguageSelectorContainer = styled.div`
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-left: 2rem;
`;

export const LanguageButton = styled.button`
    background: ${props => props.active ? props.theme.body.headers : 'transparent'};
    color: ${props => props.theme.body.text};
    border: 1px solid ${props => props.theme.body.text};
    padding: 0.4rem 0.8rem;
    cursor: pointer;
    font-size: 12px;
    font-weight: 500;
    letter-spacing: 1px;
    text-transform: uppercase;
    transition: ${props => props.theme.transition};

    &:hover {
        background: ${props => props.theme.body.headers};
        color: ${props => props.theme.body.headers};
        border-color: ${props => props.theme.body.headers};
    }

    &:active {
        transform: scale(0.98);
    }
`;
