import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { categories, getCategoryName, getSubcategoryName } from '../../posts/Posts';
import {
    CategoryFilterContainer,
    CategoryList,
    CategoryItem,
    CategoryButton,
    SubcategoryList,
    SubcategoryItem,
    SubcategoryButton,
    ClearButton
} from './Style';

const CategoryFilter = ({ onFilterChange }) => {
    const { t, i18n } = useTranslation();
    const language = i18n.language || 'en';
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [selectedSubcategory, setSelectedSubcategory] = useState(null);

    const handleCategoryClick = (categoryId) => {
        if (selectedCategory === categoryId) {
            // Deselect category
            setSelectedCategory(null);
            setSelectedSubcategory(null);
            onFilterChange(null, null);
        } else {
            // Select new category
            setSelectedCategory(categoryId);
            setSelectedSubcategory(null);
            onFilterChange(categoryId, null);
        }
    };

    const handleSubcategoryClick = (subcategoryId) => {
        if (selectedSubcategory === subcategoryId) {
            // Deselect subcategory
            setSelectedSubcategory(null);
            onFilterChange(selectedCategory, null);
        } else {
            // Select new subcategory
            setSelectedSubcategory(subcategoryId);
            onFilterChange(selectedCategory, subcategoryId);
        }
    };

    const handleClear = () => {
        setSelectedCategory(null);
        setSelectedSubcategory(null);
        onFilterChange(null, null);
    };

    const categoryKeys = Object.keys(categories);
    const selectedCategoryData = selectedCategory ? categories[selectedCategory] : null;
    const hasSubcategories = selectedCategoryData &&
        selectedCategoryData.subcategories &&
        Object.keys(selectedCategoryData.subcategories).length > 0;

    return (
        <CategoryFilterContainer>
            <CategoryList>
                {categoryKeys.map((categoryId) => {
                    const category = categories[categoryId];
                    const isSelected = selectedCategory === categoryId;

                    return (
                        <CategoryItem key={categoryId}>
                            <CategoryButton
                                onClick={() => handleCategoryClick(categoryId)}
                                active={isSelected}
                            >
                                {getCategoryName(categoryId, language)}
                            </CategoryButton>
                        </CategoryItem>
                    );
                })}
            </CategoryList>

            {hasSubcategories && (
                <SubcategoryList>
                    {Object.keys(selectedCategoryData.subcategories).map((subcategoryId) => {
                        const isSelected = selectedSubcategory === subcategoryId;

                        return (
                            <SubcategoryItem key={subcategoryId}>
                                <SubcategoryButton
                                    onClick={() => handleSubcategoryClick(subcategoryId)}
                                    active={isSelected}
                                >
                                    {getSubcategoryName(selectedCategory, subcategoryId, language)}
                                </SubcategoryButton>
                            </SubcategoryItem>
                        );
                    })}
                </SubcategoryList>
            )}

            {(selectedCategory || selectedSubcategory) && (
                <ClearButton onClick={handleClear}>
                    {t('posts.clearFilter')}
                </ClearButton>
            )}
        </CategoryFilterContainer>
    );
};

export default CategoryFilter;
