import React from 'react';
import { withTranslation } from 'react-i18next';
import marked from 'marked';
import {
    BodyContainer,
    Container,
    Image
} from '../../components/StyledComponents/StyledComponents';
import Navigation from '../../components/Navigation/Navigation.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import ProgressBar from '../../components/ProgressBar/ProgressBar';
import { getPostContent, getPosts } from '../../posts/Posts';

class BlogPost extends React.Component {

    state = {
        markdown: '',
        post: null
    }

    componentDidMount() {
        // Small delay to ensure i18n is fully initialized
        setTimeout(() => {
            this.loadPost();
        }, 100);
    }

    componentDidUpdate(prevProps) {
        // Reload post if language changed
        if (prevProps.i18n && this.props.i18n && prevProps.i18n.language !== this.props.i18n.language) {
            this.loadPost();
        }
    }

    loadPost = () => {
        const { i18n } = this.props;

        if (!i18n) {
            // i18n not ready yet, try again shortly
            setTimeout(() => this.loadPost(), 100);
            return;
        }

        // Get route from URL pathname - remove leading slash
        const pathname = window.location.pathname;
        const route = pathname.startsWith('/') ? pathname.substring(1) : pathname;

        // Get posts for current language
        const language = i18n.language || localStorage.getItem('i18nextLng') || 'en';
        const posts = getPosts(language);
        const post = posts.find(p => p.route === route);

        if (post) {
            this.setState({ post });
            fetch(post.content)
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.text()
                })
                .then(text => {
                    this.setState({
                        markdown: marked(text)
                    })
                })
                .catch(error => {
                    console.error('Error loading post:', error);
                    // Fallback to props if fetch fails
                    this.loadFallbackPost();
                })
        } else {
            console.warn('Post not found for route:', route, 'language:', language);
            // Fallback to props if route not found
            this.loadFallbackPost();
        }
    }

    loadFallbackPost = () => {
        const { content } = this.props;
        if (content) {
            fetch(content)
                .then(response => {
                    return response.text()
                })
                .then(text => {
                    this.setState({
                        markdown: marked(text)
                    })
                })
                .catch(error => {
                    console.error('Error loading fallback post:', error);
                })
        }
    }

    render() {
        const { markdown, post } = this.state;
        const { title, date, image, i18n } = this.props;

        // Use post from state if available (language-aware), otherwise use props
        // But only use props if we haven't loaded a post yet (initial load)
        const displayTitle = post ? post.title : (markdown ? title : '');
        const displayDate = post ? post.date : (markdown ? date : '');
        const displayImage = post ? post.image : image;

        return (
            <Container>
                <ProgressBar />
                <Navigation />
                <BodyContainer>
                    <Container small top={12}>
                        {displayTitle && <h1>{displayTitle}</h1>}
                        {displayDate && <p>{displayDate}</p>}
                        {displayImage && <Image src={displayImage} />}
                        {markdown && <div id={`post_${this.props.id || 'post'}`} dangerouslySetInnerHTML={{ __html: markdown }}></div>}
                        {!markdown && <p>Loading...</p>}
                    </Container>
                </BodyContainer>
                <Footer />
            </Container>
        )
    }
}

export default withTranslation()(BlogPost);
