import React, { useState, useMemo } from 'react';
import { useTranslation } from 'react-i18next';
import Navigation from '../components/Navigation/Navigation.jsx';
import Footer from '../components/Footer/Footer.jsx';
import CategoryFilter from '../components/CategoryFilter/CategoryFilter';
import Article from '../components/Article/Article';
import { BodyContainer, Container } from '../components/StyledComponents/StyledComponents';
import { ArticlesGrid } from '../components/Home/Style';
import { getPosts } from '../posts/Posts';

const Posts = () => {
    const { t, i18n } = useTranslation();
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [selectedSubcategory, setSelectedSubcategory] = useState(null);

    const allPosts = useMemo(() => getPosts(i18n.language), [i18n.language]);

    const filteredPosts = useMemo(() => {
        if (!selectedCategory) {
            return allPosts;
        }

        return allPosts.filter(post => {
            if (!post.categories || post.categories.length === 0) {
                return false;
            }

            // Check if post has the selected category
            const hasCategory = post.categories.some(
                cat => cat.categoryId === selectedCategory
            );

            if (!hasCategory) {
                return false;
            }

            // If subcategory is selected, check for it too
            if (selectedSubcategory) {
                return post.categories.some(
                    cat => cat.categoryId === selectedCategory &&
                        cat.subcategoryId === selectedSubcategory
                );
            }

            return true;
        });
    }, [allPosts, selectedCategory, selectedSubcategory]);

    const handleFilterChange = (categoryId, subcategoryId) => {
        setSelectedCategory(categoryId);
        setSelectedSubcategory(subcategoryId);
    };

    return (
        <>
            <Navigation page="posts" />
            <BodyContainer>
                <Container top={6}>
                    <Container leftAlign bottom={4}>
                        <h1>{t('posts.title')}</h1>
                    </Container>

                    <CategoryFilter onFilterChange={handleFilterChange} />

                    <ArticlesGrid>
                        {filteredPosts.map((post, index) => {
                            return (
                                <Article
                                    key={index}
                                    route={post.route}
                                    thumbnail={post.image}
                                    title={post.title}
                                    date={post.date}
                                    description={post.description}
                                />
                            );
                        })}
                    </ArticlesGrid>

                    {filteredPosts.length === 0 && (
                        <Container top={4}>
                            <p>{t('posts.noPostsFound')}</p>
                        </Container>
                    )}
                </Container>
            </BodyContainer>
            <Footer />
        </>
    );
}

export default Posts;
