import styled from 'styled-components';

export const CategoryFilterContainer = styled.div`
    margin-bottom: 3rem;
`;

export const CategoryList = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    margin-bottom: 1.5rem;
`;

export const CategoryItem = styled.div`
    display: inline-block;
`;

export const CategoryButton = styled.button`
    padding: 0.75rem 1.5rem;
    background-color: ${props => props.active
        ? props.theme.colors.primary
        : 'transparent'};
    color: ${props => props.active
        ? props.theme.colors.white
        : props.theme.body.text};
    border: 2px solid ${props => props.active
        ? props.theme.colors.primary
        : props.theme.body.text};
    border-radius: 4px;
    cursor: pointer;
    font-size: 14px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: ${props => props.theme.transition};

    &:hover {
        background-color: ${props => props.active
        ? props.theme.colors.primaryDark
        : props.theme.colors.primary};
        color: ${props => props.theme.colors.white};
        border-color: ${props => props.active
        ? props.theme.colors.primaryDark
        : props.theme.colors.primary};
    }

    &:active {
        transform: scale(0.98);
    }
`;

export const SubcategoryList = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 0.75rem;
    margin-bottom: 1rem;
    padding-left: 0.5rem;
    border-left: 2px solid ${props => props.theme.colors.primary};
`;

export const SubcategoryItem = styled.div`
    display: inline-block;
`;

export const SubcategoryButton = styled.button`
    padding: 0.5rem 1rem;
    background-color: ${props => props.active
        ? props.theme.colors.primary
        : 'transparent'};
    color: ${props => props.active
        ? props.theme.colors.white
        : props.theme.body.text};
    border: 1px solid ${props => props.active
        ? props.theme.colors.primary
        : props.theme.body.text};
    border-radius: 4px;
    cursor: pointer;
    font-size: 13px;
    font-weight: 400;
    text-transform: capitalize;
    transition: ${props => props.theme.transition};

    &:hover {
        background-color: ${props => props.active
        ? props.theme.colors.primaryDark
        : props.theme.colors.primary};
        color: ${props => props.theme.colors.white};
        border-color: ${props => props.active
        ? props.theme.colors.primaryDark
        : props.theme.colors.primary};
    }

    &:active {
        transform: scale(0.98);
    }
`;

export const ClearButton = styled.button`
    padding: 0.5rem 1rem;
    background-color: transparent;
    color: ${props => props.theme.body.text};
    border: 1px solid ${props => props.theme.body.text};
    border-radius: 4px;
    cursor: pointer;
    font-size: 12px;
    font-weight: 400;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: ${props => props.theme.transition};
    margin-top: 0.5rem;

    &:hover {
        background-color: ${props => props.theme.body.text};
        color: ${props => props.theme.colors.black};
    }

    &:active {
        transform: scale(0.98);
    }
`;
